import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import Button from "./Login/Button";
import Inputfield from "./Login/Inputfield";
import Container from "react-bootstrap/Container";
import { LinkContainer } from "react-router-bootstrap";
import history from "../history";
import ImageUpload from "./ImageUpload";
import { AiOutlineUser } from "react-icons/ai";
import { React, useState } from "react";

var lastInFront = "";
var callback;

var ip = window.localStorage.ip;

export const Navbar1 = () => {
  document.addEventListener("keydown", function (event) {
    if (event.code === "Escape") {
      hideinFront();
    }
  });
  let img;

  const [user, setUser] = useState(undefined);
  if (user === undefined) {
    fetch(localStorage.ip + "getUserProfilbild/?User=" + localStorage.aktNutzer)
      .then((response) => response.json())
      .then((data) => {
        setUser(data);
        console.error(data);
      }).catch(function(e){
        console.error(e)
        
      });
   
  }

  return (
    <div>
      <Navbar bg="black" variant="dark " className="black">
        <Navbar.Brand href="">
          <Button
            variant="secondary"
            text={
              user === undefined || user === null ? (
                <AiOutlineUser className="Icon"></AiOutlineUser>
              ) : (
                <>
                  <img
                    src={user.profilBild}
                    alt="profilbild"
                    className="reallyRound"
                  ></img>
                </>
              )
            }
            onClick={function () {
              showInFront("loginOrRegist");
            }}
          ></Button>
        </Navbar.Brand>
        <Nav className="mr-auto">
          <LinkContainer to="/">
            <Nav.Link>Entdecke</Nav.Link>
          </LinkContainer>
          <LinkContainer className="" to="/Trends">
            <Nav.Link>Trends</Nav.Link>
          </LinkContainer>
        </Nav>
      </Navbar>

      <Container className="border p-1 hide col-md-2" id="loginOrRegist">
        <Button
          variant="secondary"
          text="Login"
          className2="w-100 mb-2"
          onClick={function () {
            showInFront("login");
          }}
        ></Button>
        <Button
          variant="secondary"
          text="Registrieren"
          className2="w-100"
          onClick={function () {
            showInFront("regis");
          }}
        ></Button>
      </Container>

      <Container className="border p-4 hide" id="regis">
        <Inputfield
          ueberschrift="Username"
          type="text"
          id2="username2"
          placeholder="zb. beispielname"
        ></Inputfield>
        <Inputfield
          ueberschrift="Passwort:"
          type="password"
          id2="passwort"
          placeholder="zb. beispielpasswort"
        ></Inputfield>
        <Inputfield
          ueberschrift="E-mail:"
          type="email"
          id2="email"
          placeholder="zb. beispiel@beispiel.com"
          className2=""
        ></Inputfield>
        <div className="m-2 row mb-4 ">
          <h3 className="col-md-3 m-0">Geschlecht</h3>
          <div className="col-md-9 row pl-4">
            <p>
              <input type="radio" value="Male" name="gender" className="mr-1" />
              m√§nnlich
            </p>
            <p className="ml-2">
              <input
                type="radio"
                value="Female"
                name="gender"
                className="mr-1"
                id="frau"
              />
              weiblich
            </p>
            <p className="ml-2">
              <input
                type="radio"
                value="Other"
                name="gender"
                className="mr-1"
                id="anders"
              />
              anders
            </p>
          </div>
        </div>
        <ImageUpload
          imgID="imgRegis"
          onChange={function (can) {
            img = can.toDataURL();
          }}
        ></ImageUpload>
        <Button
          variant="secondary"
          text="Registrieren"
          className2="w-100"
          onClick={function () {
            let username = document.getElementById("username2").value;
            let passwort = document.getElementById("passwort").value;
            let mail = document.getElementById("email").value;
            let geschlecht = 0;
            if (document.getElementById("frau").checked) {
              geschlecht = 1;
            } else if (document.getElementById("anders").checked) {
              geschlecht = 2;
            }
            postData(ip + "registrieren", {
              nutzername: username,
              passwort: passwort,
              mail: mail,
              geschlecht: geschlecht,
              img: img,
            })
              .then(function (data) {
                showInFront("activate");
                console.log(data);
              })
              .catch(function (err) {
                console.error(err);
              });
          }}
        ></Button>
      </Container>

      <Container className="border p-4 hide" id="login">
        <Inputfield
          ueberschrift="Username"
          type="email"
          id2="username"
          placeholder="Username oder E-mail"
        ></Inputfield>
        <Inputfield
          ueberschrift="Passwort:"
          type="password"
          id2="Userpasswort"
          placeholder="Passwort"
        ></Inputfield>
        <Button
          variant="secondary"
          text="Login"
          onClick={function () {
            let passwort = document.getElementById("Userpasswort").value;
            let username = document.getElementById("username").value;
            postData(ip + "auth", {
              username: username,
              passwort: passwort,
            })
              .then((data) => {
                localStorage.jwt = data.jwt;
                history.push("/User");
                hideinFront();
                localStorage.aktNutzer = username;
              })
              .catch(function (e) {
                console.error(e);
              });
          }}
          className2="w-100"
        ></Button>
      </Container>

      <Container className="border p-4 hide" id="activate">
        <Inputfield
          ueberschrift="Code: "
          type="text"
          placeholder="We have sent you a Activation Code to your e-Mail"
          id2="code"
        ></Inputfield>
        <Button
          variant="secondary"
          text="Login"
          onClick={function () {
            let key = document.getElementById("code").value;
            let userName = document.getElementById("username2").value;
            postData(ip + "activate", {
              key: key,
              userName: userName,
            })
              .then((data) => {
                console.log(data);
                console.log("lade nun User Seite");
                postData(ip + "auth", {
                  username: userName,
                  passwort: document.getElementById("passwort").value,
                })
                  .then((data2) => {
                    localStorage.jwt = data2.jwt;
                    history.push("/User");
                    hideinFront();
                    localStorage.aktNutzer = userName;
                  })
                  .catch(function (e) {
                    console.error(e);
                  });
                hideinFront();
              })
              .catch(function (err) {
                console.error(err);
              });
          }}
          className2="w-100"
        ></Button>
      </Container>
    </div>
  );
};

export function showInFront(id, callback2) {
  hideinFront();
  let realID = id;
  if (document.getElementById(id) !== null) {
    document.getElementById(id).classList.remove("hide");
    document.getElementById(id).classList.add("inFront");
    document.getElementById("hintergrund").classList.remove("hide");
    document.getElementById("hintergrund").classList.add("inFront");
  }
  lastInFront = realID;
  window.addEventListener("resize", function () {
    centerElement(realID);
  });
  document.getElementById("hintergrund").addEventListener("click", function () {
    hideinFront();
  });

  callback = callback2;
  centerElement(realID);
}

export function hideinFront() {
  if (document.getElementById(lastInFront) !== null) {
    if (callback !== undefined) {
      callback();
    }
    document.getElementById(lastInFront).classList.add("hide");
    document.getElementById(lastInFront).classList.remove("inFront");
    document.getElementById("hintergrund").classList.add("hide");
    document.getElementById("hintergrund").classList.remove("inFront");
    lastInFront = null;
  }
}

function centerElement(id) {
  let x = window.innerWidth / 2;
  let ele = document.getElementById(id);
  if (ele === undefined) return;
  ele.style.right = x - ele.offsetWidth / 2 + "px";
  ele.style.top = window.pageYOffset + 100 + "px";
  document.getElementById("hintergrund").style.height =
    document.body.scrollHeight > window.innerHeight
      ? document.body.scrollHeight + "px"
      : window.innerHeight;
}
export default Navbar1;

async function postData(url = "", data = {}) {
  const response = await fetch(url, {
    method: "POST",
    mode: "cors",
    cache: "no-cache",
    credentials: "same-origin",
    headers: {
      "Content-Type": "application/json",
      charset: "UTF-8",
    },
    redirect: "follow",
    referrerPolicy: "no-referrer",
    body: JSON.stringify(data),
  });
  return response.json();
}

export async function postDataauth(url = "", data = {}) {
  const response = await fetch(url, {
    method: "POST",
    mode: "cors",
    cache: "no-cache",
    credentials: "same-origin",
    headers: {
      "Content-Type": "application/json",
      charset: "UTF-8",
      Authorization: "Bearer " + localStorage.jwt,
    },
    redirect: "follow",
    referrerPolicy: "no-referrer",
    body: JSON.stringify(data),
  });
  return response.json();
}
