import React, { useState } from "react";
import { useDropzone } from "react-dropzone";

function DragandDrop() {
  const [files, setFiles] = useState([]);

  const { getRootProps, getInputProps } = useDropzone({
    accept: "image/*",
    onDrop: (acceptedFiles) => {
      setFiles(
        Object.assign({}, {
          preview: URL.createObjectURL(acceptedFiles[0]),
        })
      );
      document.getElementById("dadPlatzhalter").classList.add("hide");
    },
  });

  const images = files.map((file) => (
    <div key={file.name} className="draganddopBild  ">
      <div className="draganddopBild">
        <img
          className="draganddopBild center autosize"
          src={file.preview}
          style={{ width: "200px" }}
          alt="preview"
          id="draganddropPic"
        />
      </div>
    </div>
  ));

  return (
    <div>
      <div className="dad" {...getRootProps()}>
        <input {...getInputProps()} />
        <div id="dadPlatzhalter">sss</div>
        <div className="draganddopBild">{images}</div>
      </div>
    </div>
  );
}

export default DragandDrop;
