import React from "react";
import Card from "react-bootstrap/Card";
import Image from "react-bootstrap/Image";
import { AiOutlineHeart, AiFillHeart } from "react-icons/ai";

const Beitrag = (props) => {
  let ersteller = props.ersteller;
  let key1 = props.key1;
  return (
    <div>
      <Card className="mb-4">
        <Card.Header className="p-0 m-0 w-100">
          <div className="rechts ">
            <img
              alt="profilbild"
              src={ersteller.profilBild}
              className="reallyRound center rechts"
            ></img>
            <h5 className="text-center"> {ersteller.nutzername}</h5>
          </div>
          <h1 className="m-3">{props.name}</h1>
        </Card.Header>
        <Image className="w-50 border center" src={props.img}></Image>
        <Card.Body>
          <p className="text1">{props.text}</p>
        </Card.Body>
        <Card.Footer>
          <div className="rechts ">
            {props.likes}
            <AiOutlineHeart
              className="rechts Icon text-secondary"
              id={"herzaus" + key1}
              onClick={function () {
                postData(
                  localStorage.ip + "like/?state=true&beitrag=" + props.name
                ).then((data) => {
                  console.error(data); // JSON data parsed by `data.json()` call
                });

                document
                  .getElementById("herzan" + key1)
                  .classList.remove("hide");
                document.getElementById("herzaus" + key1).classList.add("hide");
              }}
            ></AiOutlineHeart>
            <AiFillHeart
              className="rechts Icon text-danger hide"
              id={"herzan" + key1}
              onClick={function () {
                postData(
                  localStorage.ip + "like/?state=true&beitrag=" + props.name
                ).then((data) => {
                  console.error(data); // JSON data parsed by `data.json()` call
                });

                document.getElementById("herzan" + key1).classList.add("hide");
                document
                  .getElementById("herzaus" + key1)
                  .classList.remove("hide");
              }}
            ></AiFillHeart>
          </div>
        </Card.Footer>
      </Card>
    </div>
  );
};

async function postData(url = "") {
  // Default options are marked with *
  const response = await fetch(url, {
    method: "GET", // *GET, POST, PUT, DELETE, etc.
    mode: "cors", // no-cors, *cors, same-origin
    cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
    credentials: "same-origin", // include, *same-origin, omit
    headers: {
      "Content-Type": "application/json",
      Authorization: "Bearer " + localStorage.jwt,
      // 'Content-Type': 'application/x-www-form-urlencoded',
    },
    redirect: "follow", // manual, *follow, error
    referrerPolicy: "no-referrer", // no-referrer, *no-referrer-when-downgrade, origin, origin-when-cross-origin, same-origin, strict-origin, strict-origin-when-cross-origin, unsafe-url
  });
  return response.json(); // parses JSON response into native JavaScript objects
}

export default Beitrag;
