import Button from "./components/Login/Button";
import { React, useState, useRef } from "react";
import { Card, Container } from "react-bootstrap";
import BeitragDeck from "./components/Browsing/BeitragDeck";
import Header1 from "./components/Login/Header1";
import {
  Navbar1,
  postDataauth,
  showInFront,
  hideinFront,
} from "./components/Navbar1";
import Inputfield from "./components/Login/Inputfield";
import { fetchdata } from "./IndexPage";
import ImageUpload from "./components/ImageUpload";

const UserPage = () => {
  const ref = useRef(ImageUpload);
  const [beitraege, setBeitraege] = useState(0);
  if (beitraege.forEach === undefined) {
    fetchdata("User/?Page=0&User=" + localStorage.aktNutzer)
      .then((response) => response.json())
      .then((data) => {
        setBeitraege(data.beitraege);
        console.log(data);
      });
  }
  return (
    <div>
      <Navbar1 className="m-0"></Navbar1>
      <Header1 str="Nutzer" className="m-0"></Header1>
      <img
        className="border p-0 center w-25 mt-4"
        src="http://placekitten.com/200/200"
        alt="profilPic"
      ></img>
      <Container className="border mt-5">
        <p>
          User: Max
          <br />
          E-mail: email@beispiel.com
          <br />
          Ersteanmeldung: 27.03.2021
          <br />
          letzteAnmeldung:18.02.2021
        </p>
      </Container>

      <Container className="w-100">
        <Button
          text="neuer Post"
          className2="w-100 mt-4"
          variant="secondary"
          onClick={function () {
            showInFront("newPost", function () {
              console.log(document.getElementById("draganddropPic"));
            });
          }}
        ></Button>
      </Container>

      <Container className="hide border" id="newPost">
        <Card className="nichtRund">
          <Card.Header className="p-0 m-0">
            <Inputfield
              placeholder="Postüberschrift"
              ohneueberschrift={true}
              id2="beitragueber"
              className="w-100  p-0 nichtRund"
              className2="p-1 nichtRund"
            ></Inputfield>
          </Card.Header>

          <ImageUpload onChange={function () {}}></ImageUpload>
          <Card.Footer className="p-0">
            <textarea
              className="form-control"
              placeholder="Posttext"
              rows="5"
              id="text"
            />
          </Card.Footer>
        </Card>
        <Button
          text="neuer Post"
          className2="w-100 mt-4"
          variant="secondary"
          onClick={function () {
            console.log(ref);

            showInFront("ladehoch");
            convertToDataURLviaCanvas(
              document.getElementById("draganddropPic").src,
              function (base64Img) {
                let ueber = document.getElementById("beitragueber").value;
                let text = document.getElementById("text").value;
                postDataauth(localStorage.ip + "postBeitrag", {
                  ueberschrift: ueber,
                  text: text,
                  img: base64Img,
                })
                  .then(function () {
                    showInFront("hochgeladen");
                  })
                  .catch(function (err) {
                    console.error(err);
                  });
              }
            );
          }}
        ></Button>
      </Container>

      <Container className="border p-4 hide" id="hochgeladen">
        <h3>Beitrag wurde hochgeladen</h3>
        <Button
          text="Weiter"
          className="m-4"
          className2="m-2"
          variant="secondary"
          onClick={function () {
            hideinFront("hochgeladen");
          }}
        ></Button>
      </Container>

      <Container className="border p-4 hide" id="ladehoch">
        <h3>Beitrag wird hochgeladen</h3>
      </Container>

      <BeitragDeck className2="mt-5" data={beitraege}></BeitragDeck>
    </div>
  );
};

async function convertToDataURLviaCanvas(canvas, callback) {
  let dataURL;
  dataURL = canvas.toDataURL();
  callback(dataURL);
}

export default UserPage;
