import Button from "./Login/Button";
import { React } from "react";
import { Card, Container } from "react-bootstrap";
import { BiImages as AiFillPicture } from "react-icons/bi";
import { IoMdCamera as FaCamera } from "react-icons/io";
import DragandDrop from "./Login/DragandDrop";

const ImageUpload = (props) => {
  let onChange = props.onChange;
  return (
    <div>
      <Card.Body className="p-1 pt-3 pb-3 bg-white">
        <Container>
          <Container className="border nichtRund">
            <div id={"imgupload"+ props.imgID}>
              <Card.Body className="p-0 m-3">
                <DragandDrop
                  imgID={props.imgID}
                  onDrop={function () {
                    imgToCanvas(props.imgID, function (can) {
                      onChange(can);
                    });
                  }}
                  className="m-0 w-100"
                ></DragandDrop>
              </Card.Body>
            </div>
            <div className="hide" id="cams">
              <div id="camVideo">
                <video
                  className="center border"
                  id="video"
                  width="640"
                  height="480"
                  autoPlay
                ></video>
                <Button
                  text="Foto schieÃŸen"
                  className2="w-100 mt-4"
                  variant="secondary"
                  id="snap"
                  onClick={function () {
                    var canvas = document.getElementById("canvas" + props.imgID);
                    var context = canvas.getContext("2d");
                    var video = document.getElementById("video" + props.imgID);

                    context.drawImage(video, 0, 0, 640, 480);
                    document.getElementById("camVideo"+ props.imgID).classList.add("hide");
                    document.getElementById("canvas"+ props.imgID).classList.remove("hide");

                    onChange(document.getElementById("canvas")+ props.imgID);
                  }}
                ></Button>
              </div>
              <canvas
                id={"canvas"+ props.imgID}
                className="hide center border"
                width="640"
                height="480"
              ></canvas>
            </div>

            <Card.Footer className="mt-4 p-0 bg-white">
              <Button
                text={<AiFillPicture className="Icon" />}
                className2=""
                variant="secondary"
                onClick={function () {
                  document
                    .getElementById("cams" + props.imgID)
                    .classList.add("hide");
                  document
                    .getElementById("imgupload" + props.imgID)
                    .classList.remove("hide");
                }}
              ></Button>
              <Button
                text={<FaCamera className="Icon" />}
                className="m-4"
                className2="m-2"
                variant="secondary"
                onClick={function () {
                  document
                    .getElementById("imgupload" + props.imgID)
                    .classList.add("hide");
                  document
                    .getElementById("cams" + props.imgID)
                    .classList.remove("hide");

                  var video = document.getElementById("video"+ props.imgID);

                  // Get access to the camera!
                  if (
                    navigator.mediaDevices &&
                    navigator.mediaDevices.getUserMedia
                  ) {
                    // Not adding `{ audio: true }` since we only want video now
                    navigator.mediaDevices
                      .getUserMedia({ video: true })
                      .then(function (stream) {
                        //video.src = window.URL.createObjectURL(stream);
                        video.srcObject = stream;
                        video.play();
                      });
                  }
                }}
              ></Button>
            </Card.Footer>
          </Container>
        </Container>
      </Card.Body>
    </div>
  );
};

export default ImageUpload;

async function imgToCanvas(id, callback) {
  var img2 = document.getElementById(id);
  var img = new Image();
  img.src = img2.src;
  img.crossOrigin = "Anonymous";
  img.onload = function () {
    var canvas = document.createElement("CANVAS");
    var ctx = canvas.getContext("2d");

    canvas.height = img.height;
    canvas.width = img.width;
    ctx.drawImage(this, 0, 0);
    callback(canvas);
  };
}
