import Button from "./Login/Button";
import { React } from "react";
import { Card, Container } from "react-bootstrap";
import { BiImages as AiFillPicture } from "react-icons/bi";
import { IoMdCamera as FaCamera } from "react-icons/io";
import DragandDrop from "./Login/DragandDrop";

const ImageUpload = (props) => {
  return (
    <div>
      <Card.Body className="p-1 pt-3 pb-3">
        <Container>
          <Container className="border nichtRund">
            <div id="imgupload">
              <Card.Body className="p-0 m-3">
                <DragandDrop className="m-0 w-100"></DragandDrop>
              </Card.Body>
            </div>

            <div className="hide" id="cams">
              <div id="camVideo">
                <video id="video" width="100%" autoPlay></video>
                <Button
                  text="Foto schieÃŸen"
                  className2="w-100 mt-4"
                  variant="secondary"
                  id="snap"
                  onClick={function () {
                    var canvas = document.getElementById("canvas");
                    var context = canvas.getContext("2d");
                    var video = document.getElementById("video");
                  
                        context.drawImage(video, 0, 0, 640, 480);
                    
                    document.getElementById("camVideo").classList.add("canvas");
                    document.getElementById("camVideo").classList.remove("hide");
                  }}
                ></Button>
              </div>
              <canvas id="canvas" className = "hide" width="100%"></canvas>
            </div>

            <Card.Footer className="mt-4 p-0 bg-white">
              <Button
                text={<AiFillPicture className="Icon" />}
                className2=""
                variant="secondary"
                onClick={function () {
                  document.getElementById("cams").classList.add("hide");
                  document.getElementById("imgupload").classList.remove("hide");
                }}
              ></Button>
              <Button
                text={<FaCamera className="Icon" />}
                className="m-4"
                className2="m-2"
                variant="secondary"
                onClick={function () {
                  document.getElementById("imgupload").classList.add("hide");
                  document.getElementById("cams").classList.remove("hide");

                  var video = document.getElementById("video");

                  // Get access to the camera!
                  if (
                    navigator.mediaDevices &&
                    navigator.mediaDevices.getUserMedia
                  ) {
                    // Not adding `{ audio: true }` since we only want video now
                    navigator.mediaDevices
                      .getUserMedia({ video: true })
                      .then(function (stream) {
                        //video.src = window.URL.createObjectURL(stream);
                        video.srcObject = stream;
                        video.play();
                      });
                  }
                }}
              ></Button>
            </Card.Footer>
          </Container>
        </Container>
      </Card.Body>
    </div>
  );
};

export default ImageUpload;
