import Button from "./components/Login/Button";
import React from "react";
import { Card, Container, Form, Image } from "react-bootstrap";

import BeitragDeck from "./components/Browsing/BeitragDeck";
import Header1 from "./components/Login/Header1";
import { Navbar1, showInFront } from "./components/Navbar1";
import Inputfield from "./components/Login/Inputfield";
import { BiImages as AiFillPicture } from "react-icons/bi";
import DragandDrop from "./components/Login/DragandDrop";

const UserPage = () => {
  return (
    <>
      <Navbar1 className="m-0"></Navbar1>
      <Header1 str="Nutzer" className="m-0"></Header1>
      <img
        className="border p-0 center w-25 mt-4"
        src="http://placekitten.com/200/200"
      ></img>
      <Container className="border mt-5">
        <p>
          User: Max
          <br />
          E-mail: email@beispiel.com
          <br />
          Ersteanmeldung: 27.03.2021
          <br />
          letzteAnmeldung:18.02.2021
        </p>
      </Container>

      <Container>
        <Button
          text="neuer Post"
          className2="w-100 mt-4"
          variant="secondary"
          onClick={function () {
            showInFront(<newPost></newPost>);
          }}
        ></Button>
      </Container>

      <BeitragDeck className=""></BeitragDeck>
    </>
  );
};

const newPost = () => {
  return (
    <Container className="hide border" id="newPost">
      <Card className="nichtRund">
        <Card.Header className="p-0 m-0">
          <Inputfield
            placeholder="Postüberschrift"
            ohneueberschrift={true}
            className="w-100  p-0 nichtRund"
            className2="p-1 nichtRund"
          ></Inputfield>
        </Card.Header>

        <Card.Body className="p-0">
          <Container>
            <Container className="border nichtRund">
              <Card.Body className="p-0 m-3">
                <DragandDrop className="m-0"></DragandDrop>
              </Card.Body>
              <Card.Footer className="mt-4 p-0 bg-white">
                <Button
                  text={<AiFillPicture className="Icon" />}
                  className2=""
                  variant="secondary"
                  onClick={function () {
                    showInFront("newPost");
                  }}
                ></Button>
                <Button
                  text={<AiFillPicture className="Icon" />}
                  className="m-4"
                  className2="m-2"
                  variant="secondary"
                  onClick={function () {
                    showInFront("newPost");
                  }}
                ></Button>
              </Card.Footer>
            </Container>
          </Container>
        </Card.Body>

        <Card.Footer className="p-0">
          <Inputfield
            placeholder="Text"
            ohneueberschrift={true}
            className="w-100 "
            className2="p-1"
          ></Inputfield>
        </Card.Footer>
      </Card>

      <Button
        text="neuer Post"
        className2="w-100 mt-4"
        variant="secondary"
        onClick={function () {}}
      ></Button>
    </Container>
  );
};

export default UserPage;
