import React from "react";
import { Row, Col } from "react-bootstrap";
import Card from "react-bootstrap/Card";
import Image from "react-bootstrap/Image";

const Beitrag = (props) => {
  let ersteller = props.ersteller;
  console.error(ersteller);
  return (
    <div>
      <Card className="mb-4">
        <Card.Header className="p-0 m-0" >
          <Row className="p-0 m-0 rechts">
            <Col sm={12}>
              <Row>
                <Col sm = {7} className="text-right bg-danger">{ersteller.nutzername}</Col>
                <Col sm = {4} className="bg-info">
                  <img src={ersteller.profilBild} className="reallyRound"></img>
                </Col>
              </Row>
            </Col>
          </Row>
        </Card.Header>
        <Image className="w-50 border center" src={props.img}></Image>
        <Card.Body>
          <p>{props.text}</p>
        </Card.Body>
      </Card>
    </div>
  );
};

export default Beitrag;
