import React from "react";
import { Row, Col } from "react-bootstrap";
import Card from "react-bootstrap/Card";
import Image from "react-bootstrap/Image";

const Beitrag = (props) => {
  let ersteller = props.ersteller;
  console.error(ersteller);
  return (
    <div>
      <Card className="mb-4">
        <Card.Header className="p-0 m-0 w-100">
          <Row className="p-0 m-0 rechts bg-info ">
            <Col md={10}>asd</Col>
            <Col md={2}>
              <Row>
                <Col md={6.2} className="text-right">
                  {ersteller.nutzername}
                </Col>
                <Col md={4} className="">
                  <img src={ersteller.profilBild} className="reallyRound"></img>
                </Col>
              </Row>
            </Col>
          </Row>
          <div className="rechts">
          {ersteller.nutzername}
            <img src={ersteller.profilBild} className="reallyRound"></img>
          </div>
        </Card.Header>
        <Image className="w-50 border center" src={props.img}></Image>
        <Card.Body>
          <p>{props.text}</p>
        </Card.Body>
      </Card>
    </div>
  );
};

export default Beitrag;
