import React from "react";
import { Row, Col } from "react-bootstrap";
import Card from "react-bootstrap/Card";
import Image from "react-bootstrap/Image";

const Beitrag = (props) => {
  let ersteller = props.ersteller;
  console.error(ersteller);
  return (
    <div>
      <Card className="mb-4">
        <Card.Header className="p-0">
          <Row>
            <Col sm>sm=true</Col>
            <Col sm={2}>
              <Row>
                <Col sm = {1}>{ersteller.nutzername}</Col>
                <Col sm = {1}>
                  <img src={ersteller.profilBild} className="reallyRound"></img>
                </Col>
              </Row>
            </Col>
          </Row>
        </Card.Header>
        <Image className="w-50 border center" src={props.img}></Image>
        <Card.Body>
          <p>{props.text}</p>
        </Card.Body>
      </Card>
    </div>
  );
};

export default Beitrag;
