import Button from "./Login/Button";
import { React, useState } from "react";
import { Card, Container } from "react-bootstrap";
import { BiImages as AiFillPicture } from "react-icons/bi";
import { IoMdCamera as FaCamera } from "react-icons/io";
import DragandDrop from "./Login/DragandDrop";

const ImageUpload = (props) => {
  const [mode, setMode] = useState(0);
  const angezeigtes = (
    <Card.Body className="p-0 m-3">
      <DragandDrop className="m-0 w-100"></DragandDrop>
    </Card.Body>
  );

  if (mode === 1) {
    if (navigator.mediaDevices && navigator.mediaDevices.getUserMedia) {
      // Not adding `{ audio: true }` since we only want video now
      navigator.mediaDevices
        .getUserMedia({ video: true })
        .then(function (stream) {
          //video.src = window.URL.createObjectURL(stream);
          angezeigtes = (
            <>
              <video
                id="video"
                width="640"
                height="480"
                autoPlay={true}
                src={stream}
              ></video>
              <button id="snap">Snap Photo</button>
              <canvas id="canvas" width="640" height="480"></canvas>
            </>
          );
          console.log("da")
          useForceUpdate()
        });
    }
    // Get access to the camera!
  }

  return (
    <div>
      <Card.Body className="p-1 pt-3 pb-3">
        <Container>
          <Container className="border nichtRund">
            {angezeigtes}
            <Card.Footer className="mt-4 p-0 bg-white">
              <Button
                text={<AiFillPicture className="Icon" />}
                className2=""
                variant="secondary"
                onClick={function () {
                  setMode(0);
                }}
              ></Button>
              <Button
                text={<FaCamera className="Icon" />}
                className="m-4"
                className2="m-2"
                variant="secondary"
                onClick={function () {
                  setMode(1);
                  console.log("da")
                }}
              ></Button>
            </Card.Footer>
          </Container>
        </Container>
      </Card.Body>
    </div>
  );
};

export default ImageUpload;
