import React from "react";
import Card from "react-bootstrap/Card";
import Image from "react-bootstrap/Image";
import { AiOutlineHeart,AiFillHeart } from "react-icons/ai";

const Beitrag = (props) => {
  let ersteller = props.ersteller;
  let key = props.key;
  return (
    <div>
      <Card className="mb-4">
        <Card.Header className="p-0 m-0 w-100">
          <div className="rechts ">
            <img
              alt="profilbild"
              src={ersteller.profilBild}
              className="reallyRound center rechts"
            ></img>
            <h5 className="text-center"> {ersteller.nutzername}</h5>
          </div>
          <h1 className="m-3">{props.name}</h1>
        </Card.Header>
        <Image className="w-50 border center" src={props.img}></Image>
        <Card.Body>
          <p className="text1">{props.text}</p>
        </Card.Body>
        <Card.Footer>
          <AiOutlineHeart
            className="rechts Icon text-secondary"
            id={"herzaus" + key}
            onClick={function () {
              document.getElementById("herzan" + key).classList.remove("hide")
              document.getElementById("herzaus" + key).classList.add("hide")
            }}
          ></AiOutlineHeart>
          <AiFillHeart
            className="rechts Icon text-danger hide"
            id={"herzan" + key}
            onClick={function () {
              document.getElementById("herzan" + key).classList.add("hide")
              document.getElementById("herzaus" + key).classList.remove("hide")
            }}
          ></AiFillHeart>
        </Card.Footer>
      </Card>
    </div>
  );
};

export default Beitrag;
