import Button from "./Login/Button";
import { React, useState } from "react";
import { Card, Container } from "react-bootstrap";
import BeitragDeck from "./Browsing/BeitragDeck";
import Header1 from "./Login/Header1";
import { showInFront } from "./Navbar1";
import { BiImages as AiFillPicture } from "react-icons/bi";
import DragandDrop from "./Login/DragandDrop";
import ImageUpload from "./ImageUpload";

const ImageUploader = () => {
  return (
    <div>
      <Card.Body className="p-1 pt-3 pb-3">
        <Container>
          <Container className="border nichtRund">
            <Card.Body className="p-0 m-3">
              <DragandDrop className="m-0 w-100"></DragandDrop>;
            </Card.Body>
            <Card.Footer className="mt-4 p-0 bg-white">
              <Button
                text={<AiFillPicture className="Icon" />}
                className2=""
                variant="secondary"
                onClick={function () {
                  showInFront("newPost");
                }}
              ></Button>
              <Button
                text={<AiFillPicture className="Icon" />}
                className="m-4"
                className2="m-2"
                variant="secondary"
                onClick={function () {
                  showInFront("newPost");
                }}
              ></Button>
            </Card.Footer>
          </Container>
        </Container>
      </Card.Body>
    </div>
  );
};

export default ImageUpload;
