      document.getElementById("dadPlatzhalter").classList.add("hide");
    },
  });

  const images = files.map((file) => (
    <div key={file.name} className=" w-100 ">
      <div className="w-100">
        <img
          className="w-100 rounded-lg center autosize"
          src={file.preview}
          style={{ width: "200px" }}
          alt="preview"
          id = "draganddropPic"
        />
      </div>
    </div>
  ));

  return (
    <div>
      <div className="dad" {...getRootProps()}>
        <input {...getInputProps()} />
        <div className = "w-100 " id="dadPlatzhalter">sss</div>
        <div className="draganddopBild w-100">{images}</div>
      </div>
    </div>
  );
}

export default DragandDrop;
